import React, {useState} from 'react';
import './FAQs.css';
import {Link, useNavigate} from "react-router-dom";
import { Helmet } from 'react-helmet';
import axios from "axios";
import Cookies from "js-cookie";

const Faqs = () => {
    const navigate = useNavigate();

    const getUserRole = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8000/read_user/${userId}/`);
            return response.data.role;
        } catch (error) {
            console.error("Failed to fetch user role:", error);
            return null;
        }
    };

    const handleUserIconClick = async (e) => {
        e.preventDefault();
        const userId = localStorage.getItem('user_id');
        if (Cookies.get('auth_token') && userId) {
            const role = await getUserRole(userId);
            if (role === 'user') {
                navigate('/profile/personal_information');
            } else if (role === 'admin') {
                navigate('/admin/performance_overview');
            } else {
                navigate('/sign_in');
            }
        } else {
            console.log("Something Wrong !!")
        }
    };

    const [openIndex, setOpenIndex] = useState(null);

    const handleClick = (index) => {
      setOpenIndex(openIndex === index ? null : index);
    };


    const items = [
        {
            title: 'How can I pay for my order ?',
            content: 'Contenu de la section 1...',
        },
        {
            title: 'Can I return an item ?',
            content: 'Contenu de la section 2...',
        },
        {
            title: 'How long my order will take to be delivered ?',
            content: 'Contenu de la section 3...',
        },
        {
            title: 'How can I change something in my order ?',
            content: 'Contenu de la section 3...',
        },
    ];

    return (
        <div>

            <Helmet>
                <title>FAQs</title>
            </Helmet>

            <nav className="navbar">

                <div className="top">
                    <img src="/images/Home/logo.jpg" alt="Logo"/>
                </div>

                <div className="bottom">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <Link to="/" className="nav-link">Home</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/shop" className="nav-link">Shop</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/aboutus" className="nav-link">About Us</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/faqs" className="nav-link" style={{color: '#9E5AC7'}}>FAQs</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/contact" className="nav-link">Contact</Link>
                        </li>
                    </ul>

                    <ul className="icons">
                        <li>
                            <Link to="/cart">
                                <i className="fas fa-shopping-cart"></i>
                            </Link>
                        </li>
                        <li>
                            <Link to="/wishlist">
                                <i className="fas fa-heart"></i>
                            </Link>
                        </li>
                        <li>
                            <Link to="/profile" onClick={handleUserIconClick}>
                                <i className="fas fa-user"></i>
                            </Link>
                        </li>
                    </ul>
                </div>

            </nav>


            <div className="Header1-Shop">

                <div className="image1-shop">
                    <img src="/images/FAQs/image1-faqs.jpg" alt="Logo"/>
                </div>
                <div className="content1-shop">
                    <h3>Frequently Asked Questions</h3>
                </div>

            </div>


            <div className="Header1-Faqs">

                {items.map((item, index) => (
                    <div key={index}>
                        <div className="item-title" onClick={() => handleClick(index)}>
                            <strong>{item.title}</strong>
                        </div>
                        {openIndex === index &&
                            <div className="item-content">{item.content}</div>}
                    </div>
                ))}

            </div>


            <div className="footer">
                <div className="footer-content">

                    <div className="footer-logo">
                        <img src="/images/Home/logo.jpg" alt="Logo"/>
                    </div>

                    <div className="footer-info">
                        <h2>Information</h2>
                        <ul>
                            <li>Delivery Information</li>
                            <li>
                                <Link to="/faqs"
                                      style={{color: 'rgba(255,255,255,0.6)', textDecoration: 'none'}}>FAQs</Link>
                            </li>
                            <li>
                                <Link to="/contact"
                                      style={{color: 'rgba(255,255,255,0.6)', textDecoration: 'none'}}>Contact</Link>
                            </li>
                        </ul>
                    </div>

                    <div className="footer-contact">
                        <h2>Get In Touch</h2>
                        <p>We'd love to hear from you.</p>
                        <ul className="icons">
                            <li>
                                <Link to="/">
                                    <i className="fab fa-instagram"></i>
                                </Link>
                            </li>
                            <li>
                                <Link to="/">
                                    <i className="fab fa-whatsapp"></i>
                                </Link>
                            </li>
                            <li>
                                <Link to="/">
                                    <i className="fab fa-facebook"></i>
                                </Link>
                            </li>
                        </ul>

                    </div>

                </div>

                <hr></hr>

                <div className="footer-copyright">
                    <p>Copyright Â© 2024. All Rights Reserved.</p>
                </div>

            </div>


        </div>

    );
};

export default Faqs;
